import React from 'react';

export default class SiswaBox extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      nama: '',
      umur: ''
    }

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeUmur = this.onChangeUmur.bind(this);
    this.onSubmitForm = this.onSubmitForm.bind(this);
  }

  onChangeName(event){
    this.setState({nama: event.target.value});
  }

  onChangeUmur(event){
    this.setState({umur: event.target.value});
  }

  onSubmitForm(event){
    event.preventDefault();
    this.props.addData(this.state);
    this.setState({
      nama: '',
      umur: ''
    });
  }


  render(){
    return(
      <form onSubmit={this.onSubmitForm}>
      <div className="form-group row">
      <label className="col-sm-2 col-form-label">Nama</label>
      <div className="col-sm-10">
      <input className="form-control" type="text" value={this.state.nama} placeholder="ketikkan nama" onChange={this.onChangeName} />
      </div>
      </div>
      <div className="form-group row">
      <label className="col-sm-2 col-form-label">Umur</label>
      <div className="col-sm-10">
      <input className="form-control" type="text" value={this.state.umur} placeholder="ketikkan umur" onChange={this.onChangeUmur} />
      </div>
      </div>
      <div className="form-group row">
      <div className="col-sm-10">
      <button className="btn btn-primary" type="submit">Simpan</button>
      </div>
      </div>
      </form>
    )
  }
}
