import React from 'react';

import SiswaTable from './SiswaTable';
import SiswaForm from './SiswaForm';

import axios from 'axios';

const request = axios.create({
  baseURL: 'http://localhost:3000',
  timeout: 1000,
  headers: {'X-Custom-Header': 'foobar'}
});

export default class SiswaBox extends React.Component {
  constructor(props){
    super(props)
    this.state = {data: []}
  }

  componentDidMount(){
    request.get('/users')
    .then(function (response) {
      this.setState({data: response.data})
    }.bind(this))
    .catch(function (error) {
      // handle error
      console.log(error);
    })
  }

  addData = (siswa) => {
    this.setState((prevState)=> ({
      data: [...prevState.data, siswa]
    }))
    request.post('/users',{
      nama: siswa.nama,
      umur: siswa.umur
    })
    .then(function (response) {

    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })
  }

  render(){
    console.log(this.state.data);
    return (
      <div className="container">
      <h1>Daftar Siswa</h1>
      <SiswaTable data={this.state.data} />
      <SiswaForm addData={this.addData} />
      </div>
    );
  }
}
