import React, { Component } from 'react';
class SiswaItem extends Component{
 constructor(props){
   super(props);
   this.state = {
    editButton: false,
    id: props.id,
            name: props.name,
            type: props.type
   }
   this.handleEditOn = this.handleEditOn.bind(this)
   this.handleEditOff = this.handleEditOff.bind(this)
 }

 handleNameChange = e => {
  const name = e.target.name;
  // const type = e.target.type;
  this.setState({ [name]: e.target.value });
  // this.setState({ [type]: e.target.value });
};
 handleEditOn(e) {
  e.preventDefault();
  this.setState({ editButton: true });
}

handleEditOff(e) {
  e.preventDefault()
  this.setState({ editButton: false })

}

handleEditSave = (e) => {
  e.preventDefault();
  const { idUser, name, phone } = this.state;
  if (name && phone) {
      this.props.putPhonebook(idUser, name, phone)
      this.setState({ editButton: false });
  }
  console.log('data handleEditSave > ', idUser, name, phone);

}

  render(){
      return (

        <tr>
        <th scope="row">{this.props.meals.id}</th>
        {!this.state.editButton && (
            <>
                <td>{this.props.meals.name}</td>
                <td>{this.props.meals.type}</td>  
                <td>
                    <button type="submit" className="btn btn-success mb-2" onClick={this.handleEditOn}>Edit</button>

                </td>
            </>
        )}
        {this.state.editButton && (
            <>
                <td>
                    <div className="form-check mb-2 mr-sm-2">
                        <input type="text"
                            className="form-control mb-2 mr-sm-2"
                            name="name"
                            value={this.state.name}
                            placeholder="name"
                            onChange={this.handleNameChange.bind(this)} />
                    </div>
                </td>
                <td>
                    <div className="form-check mb-2 mr-sm-2">
                        {/* <input type="text"
                            className="form-control mb-2 mr-sm-2"
                            placeholder="type"
                            name="type"
                            value={this.state.type}
                            onChange={this.handleNameChange.bind(this)} /> */}
                            <select className="form-control" name="type" value={this.state.type} onChange={this.handleNameChange.bind(this)}>

              <option value="appetizer">appetizer</option>

              <option value="main-course">main-course</option>

              <option value="dessert">dessert</option>
            </select>
                    </div>
                </td>
                <td>
                    <button type="submit" className="btn btn-primary mb-2" onClick={this.handleEditSave}>save</button>
                    <button type="submit" className="btn btn-danger mb-2" onClick={this.handleEditOff}>Cancel</button>
                </td>


            </>
        )}
    </tr>
        // <tr>          
        // <td>{this.props.meals.name}</td>
        // <td>{this.props.meals.type}</td>
        // <td>
        // <button className="btn btn-success" type="button" onClick={this.onEdit}>Edit</button>
        // </td>
        // </tr>
      )

  }
}

