import React, { Component } from 'react';
class SiswaItem extends Component{
 constructor(props){
   super(props);
   this.state ={
     isEdit:false
   }
   this.onEdit = this.onEdit.bind(this);
 }
 onEdit(){
   this.setState({isEdit: true})
 }

  render(){

    if(this.props.meals.sent){
      return (      

        <tr>{this.state}
        <td>{this.props.meals.name}</td>
        <td>{this.props.meals.type}</td>
        <td>
        <button className="btn btn-success" type="button" onClick={this.onEdit}>Edit</button>
        {/* <button className="btn btn-danger" type="button">Delete</button> */}
        </td>
        </tr>
      )
    }else{
      return (
        <tr>
        <td>{this.props.meals.name}</td>
        <td>{this.props.meals.type}</td>
        <td>
        <button className="btn btn-warning" type="button">Resend</button>
        </td>
        </tr>
      )
    }
  }
}

