import React from 'react';

import SiswaTable from './SiswaTable';
import SiswaForm from './SiswaForm';

import axios from 'axios';
const API_URL = 'http://api.wibs.sch.id/v2/meal/post';

const request = axios.create({
  baseURL: API_URL,
  timeout: 1000,
  headers: {'Application-Token': 'geSzgVahOlowulcgHEtQmu9Ybofk1lRnPFd3V5atSEu1SD1dt2'}
});

export default class SiswaBox extends React.Component {
  constructor(props){
    super(props)
    this.state = {data: []}
  }

  componentDidMount(){
    request.post('http://api.wibs.sch.id/v2/meal/post/datatable.food-category',{
      headers:{'Application-Token': 'geSzgVahOlowulcgHEtQmu9Ybofk1lRnPFd3V5atSEu1SD1dt2' }
    })
    .then(function (response) {
      let data = response.data.map(item => {
        item.sent = true;
        return item;
      })
      this.setState({data: data});
      console.log(response.data);

    }.bind(this))
    .catch(function (error) {
      // handle error
      console.log(error);
    })
  }

  componentDidMount() {
    const self = this;
    axios.get(`${API_URL}/phonebooks`).then((result) => {
        // console.log(result.data);
        self.setState({
            users: result.data,
        });
    })
        .catch(error => console.log(error));

    // this.setState({
    //     users:[
    //         {id: 1, name:"taufik"},
    //         {id: 2, name:"syamil"},
    //         {id: 3, name:"sakha"},

    //     ]
    // })
}

  addData = (siswa) => {
    let id = Date.now();
    this.setState((prevState)=> ({
      data: [...prevState.data, {id, name: siswa.name, type: siswa.type, sent: true}]
    }))
    request.post('/users',{
      id: id,
      name: siswa.name,
      type: siswa.type
    })
    .then(function (response) {

    })
    .catch(function (error) {
      this.setState((prevState)=> ({
        data: prevState.data.map(item => {
          if(id === item.id){
            item.sent = false;
          }
          return item;
        })
      }))
    }.bind(this))
  }

  render(){
    console.log(this.state.data);
    return (
      <div className="container">
      <h1>Meals List</h1>
      <SiswaTable data={this.state.data} />
      <SiswaForm addData={this.addData} />
      </div>
    );
  }
}
