import React, { Component } from 'react';
class SiswaItem extends Component{
 constructor(props){
   super(props);
   this.state = {
    editButton: false,
    id: props.id,
            name: props.name,
            type: props.type
   }
   this.handleEditOn = this.handleEditOn.bind(this)
   this.handleEditOff = this.handleEditOff.bind(this)
 }

 handleNameChange = e => {
  const name = e.target.name;
  // const phone = e.target.phone;
  this.setState({ [name]: e.target.value });
  // this.setState({ [phone]: e.target.value });
};
 handleEditOn(e) {
  e.preventDefault();
  this.setState({ editButton: true });
}

handleEditOff(e) {
  e.preventDefault()
  this.setState({ editButton: false })

}

  render(){
      return (

        <tr>
        {/* <th scope="row">{this.props.meals.id}</th> */}
        {!this.state.editButton && (
            <>
                <td>{this.props.meals.name}</td>
                <td>{this.props.meals.type}</td>  
                <td>
                    <button type="submit" className="btn btn-success mb-2" onClick={this.handleEditOn}>Edit</button>

                </td>
            </>
        )}
        {this.state.editButton && (
            <>
                <td>
                    <div className="form-check mb-2 mr-sm-2">
                        <input type="text"
                            className="form-control mb-2 mr-sm-2"
                            name="name"
                            value={this.state.name}
                            placeholder="name"
                            onChange={this.handleNameChange.bind(this)} />
                    </div>
                </td>
                <td>
                    <div className="form-check mb-2 mr-sm-2">
                        <input type="text"
                            className="form-control mb-2 mr-sm-2"
                            placeholder="phone"
                            name="phone"
                            value={this.state.phone}
                            onChange={this.handleNameChange.bind(this)} />
                    </div>
                </td>
                <td>
                    <button type="submit" className="btn btn-primary mb-2" onClick={this.handleEditSave}>save</button>
                    <button type="submit" className="btn btn-danger mb-2" onClick={this.handleEditOff}>Cancel</button>
                </td>


            </>
        )}
    </tr>
        // <tr>          
        // <td>{this.props.meals.name}</td>
        // <td>{this.props.meals.type}</td>
        // <td>
        // <button className="btn btn-success" type="button" onClick={this.onEdit}>Edit</button>
        // </td>
        // </tr>
      )

  }
}

