import React, { Component } from 'react';
class SiswaItem extends Component{
  constructor(props){
    super(props)
        this.state = {
            editButton: false,
            idUser: props.idUser,
            name: props.name,
            phone: props.phone
        }
        this.handleEditOn = this.handleEditOn.bind(this)
        this.handleEditOff = this.handleEditOff.bind(this)
  }
  handleEditOn(e) {
    e.preventDefault();
    this.setState({ editButton: true });
}

  render(){
    if(this.props.meals.sent){
      return (
        <tr>
        <td>{this.props.meals.name}</td>
        <td>{this.props.meals.type}</td>
        <td>
        <button className="btn btn-success" type="button" onClick={this.handleEditOn}>Edit</button>
        {/* <button className="btn btn-danger" type="button">Delete</button> */}
        </td>
        </tr>
      )
    }else{
      return (
        <tr>
        <td>{this.props.meals.name}</td>
        <td>{this.props.meals.type}</td>
        <td>
        <button className="btn btn-warning" type="button">Resend</button>
        </td>
        </tr>
      )
    }
  }
}

